import './App.css';
import React from 'react';
import Cookies from 'universal-cookie';
const cookies = new Cookies();

export default class App extends React.Component {
  constructor(){
    super();
    this.handleColor = this.handleColor.bind(this);
    this.handleReload = this.handleReload.bind(this);
  };
  handleColor(e){
    cookies.set(`Color`,`${e.target.value}`, { path:'/' });
  }
  handleReload(e){
    e.preventDefault();
    window.location.reload();
  }
  render(){
    return (
      <div>
        <div className="buttons">
          <h1 style={{color:cookies.get(`Color`)}}>Elige un color</h1>
          <button onClick={this.handleColor} value="Red">Red</button>
          <button onClick={this.handleColor} value="blue">Blue</button>
          <button onClick={this.handleReload}>Actualizar</button>
        </div>
      </div>
    );

  };

}
