import './App.css';
import React from 'react';
import Cookies from 'universal-cookie';
const cookies = new Cookies();

export default class App extends React.Component {
  constructor(){
    super();
    this.handleTipo = this.handleTipo.bind(this);
    this.handleReload = this.handleReload.bind(this);
  };
  handleTipo(e){
    cookies.set(`text-transform:`,`${e.target.value}`, { path:'/' });
  }
  handleReload(e){
    e.preventDefault();
    window.location.reload();
  }
  render(){
    return (
      <div>
        <div className="buttons">
          <h1 style={{text:cookies.get(`text-transform:`)}}>Selecciona un color</h1>
          <button onClick={this.handleTipo} value="uppercase">Mayuscula</button>
          <button onClick={this.handleTipo} value="lowercase">Minuscula</button>
          <button onClick={this.handleReload}>Actualizar</button>
        </div>
      </div>
    );

  };

}
